import React from 'react'
const commentDatas = [
  {
   id:1,
   name:'Manoj',
   text:"This is a comment",
   replies: []
},
{
  id:2,
  name:'Sobhan',
  text:"This is a comment2",
  replies: [
       { 
        id:1,
        name:'Manoj',
        text:"This is a comment",
        replies: []
      },
      { 
        id:1,
        name:'Manoj',
        text:"This is a comment",
        replies: [
          {
            id:1,
            name:'Manoj',
            text:"This is a comment",
            replies: []
          }
        ]
      },
  ]
},
];
const Comment = (({data})=>{

    const {name, text, replies} = data;
    return (<div className='flex shadow-sm bg-gray-100 p-2 rounded-lg  my-2'>
        <img className="h-8 w-8" alt="user" src='data:image/png;base64,' />
     <div className='px-3'>
        <p className='text-bold'>Name</p>
        <p>Comment</p>

     </div>
    </div>)

})
const CommentList = ({comments}) =>{
  return comments.map((comment, index)=>(
    <div>
    <Comment data={comment} key={index} />
    <div className='ml-5 border border-l-black ml-5'>
    <CommentList comments={comment.replies} key={index} />

    </div>
    </div>
  ))

}
const CommentsContainer = () => {
 
  return (
    <div className='m-5 p-2'>
     <h1 className='text-2xl font-bold'>Comments:</h1>    
     <CommentList comments={commentDatas} />
    </div>
  )
}

export default CommentsContainer
